@inherits Umbraco.Web.Mvc.UmbracoTemplatePage
@{
    Layout = null;
}

@{
    IEnumerable<IPublishedContent> rootNode = Umbraco.TypedContentAtRoot();
    var home = rootNode.Where(x => x.IsDocumentType("USNHomepage")).First();

	Response.Redirect(home.Url);
}

@inherits Umbraco.Web.Mvc.UmbracoTemplatePage
@{
    Layout = "USNMaster.cshtml";

    if (!String.IsNullOrEmpty(Request.QueryString["signout"]))
    {
        FormsAuthentication.SignOut();
        Context.Response.Redirect(Model.Content.Url, true);
    }
}

